package com.mjw.suanfa.com;

import java.util.Scanner;

public class Test1 {

	public static void main(String[] args) {
		while (true) {
			@SuppressWarnings("resource")
			Scanner sc = new Scanner(System.in);
			int month = sc.nextInt();
			test(month);
		}
	}

	// 传入参数代表月份
	public static void test(int month) {
		int lastMonthCount = 1;
		int nowMonthCount = 1;
		int temp;
		for (int i = 3; i <= month; i++) {
			temp = nowMonthCount;
			nowMonthCount = lastMonthCount + nowMonthCount;
			lastMonthCount = temp;
		}
		System.out.println(nowMonthCount);
	}

}
